export default {
  name: 'ExecutionOptions',
  props: {
    stopOnError: {
      type: Boolean,
      default: true
    },
    isReplaying: {
      type: Boolean,
      default: false
    },
    stepInterval: {
      type: Number,
      default: 500 // Valeur par défaut: 500ms entre les étapes
    }
  },
  data() {
    return {
      localStopOnError: this.stopOnError,
      localStepInterval: this.stepInterval
    };
  },
  watch: {
    stopOnError(newVal) {
      this.localStopOnError = newVal;
    },
    stepInterval(newVal) {
      this.localStepInterval = newVal;
    }
  },
  methods: {
    updateStopOnError(value) {
      this.$emit('update:stop-on-error', value);
    },
    updateStepInterval(value) {
      // Convertir en nombre et émettre l'événement
      const interval = parseInt(value) || 0;
      this.$emit('update:step-interval', interval);
    }
  }
};
