export default {
  name: 'StepsList',
  props: {
    steps: {
      type: Array,
      required: true
    },
    expandedStep: {
      type: Number,
      default: null
    }
  },
  data() {
    return {
      localActiveTab: 'details'
    };
  },
  methods: {
    updateStepEnabled(index, enabled) {
      const updatedStep = {
        enabled,
        skip: !enabled
      };
      this.$emit('update-step', index, updatedStep);
    },
    getMethodColor(method) {
      const colors = {
        GET: 'primary',
        POST: 'success',
        PUT: 'warning',
        DELETE: 'error',
        PATCH: 'info'
      };
      return colors[method] || 'grey';
    },
    hasVariables(step) {
      return step.variableSubstitutions && step.variableSubstitutions.length > 0 || step.variableCaptures && step.variableCaptures.length > 0;
    }
  }
};
