export default {
  name: 'RecordControls',
  props: {
    isRecording: {
      type: Boolean,
      default: false
    },
    recordedCount: {
      type: Number,
      default: 0
    },
    status: {
      type: String,
      default: 'Ready to record'
    },
    hasRecordedData: {
      type: Boolean,
      default: false
    },
    disabled: {
      type: Boolean,
      default: false
    }
  },
  methods: {
    toggleRecording() {
      this.$emit('toggle-recording');
    },
    exportRecording() {
      this.$emit('export-recording');
    },
    clearRecordings() {
      this.$emit('clear-recordings');
    },
    openReplayTab() {
      chrome.tabs.create({
        url: 'replay.html'
      });
    }
  }
};
