export default {
  name: 'ExecutionOptions',
  props: {
    stopOnError: {
      type: Boolean,
      default: true
    },
    isReplaying: {
      type: Boolean,
      default: false
    },
    stepInterval: {
      type: Number,
      default: 0
    }
  },
  data() {
    return {
      localStopOnError: this.stopOnError,
      localStepInterval: this.stepInterval
    };
  },
  watch: {
    stopOnError(newVal) {
      this.localStopOnError = newVal;
    },
    stepInterval(newVal) {
      this.localStepInterval = newVal;
    }
  },
  methods: {
    updateStopOnError(value) {
      this.$emit('update:stop-on-error', value);
    },
    updateStepInterval(value) {
      this.$emit('update:step-interval', value);
    }
  },
  mounted() {
    // Émettre un événement pour indiquer que la progression en temps réel est toujours active
    this.$nextTick(() => {
      this.$emit('update:show-runtime-progress', true);
    });
  }
};
