export default {
  name: 'ExecutionOptions',
  props: {
    stopOnError: {
      type: Boolean,
      default: true
    },
    isReplaying: {
      type: Boolean,
      default: false
    },
    stepInterval: {
      type: Number,
      default: 0
    },
    showRuntimeProgress: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      localStopOnError: this.stopOnError,
      localStepInterval: this.stepInterval,
      localShowRuntimeProgress: this.showRuntimeProgress
    };
  },
  watch: {
    stopOnError(newVal) {
      this.localStopOnError = newVal;
    },
    stepInterval(newVal) {
      this.localStepInterval = newVal;
    },
    showRuntimeProgress(newVal) {
      this.localShowRuntimeProgress = newVal;
    }
  },
  methods: {
    updateStopOnError(value) {
      this.$emit('update:stop-on-error', value);
    },
    updateStepInterval(value) {
      this.$emit('update:step-interval', value);
    },
    updateShowRuntimeProgress(value) {
      this.$emit('update:show-runtime-progress', value);
    }
  }
};
