export default {
  name: 'ReplayHeader',
  props: {
    executionStatus: {
      type: String,
      default: 'Ready to start'
    },
    autoLoadedData: {
      type: Boolean,
      default: false
    }
  },
  methods: {
    triggerFileInput() {
      // Trigger the hidden file input
      this.$refs.fileInput.click();
    },
    onFileSelected(event) {
      // Pass the file to the parent component
      const file = event.target?.files?.[0];
      if (file) {
        this.$emit('load-file', file);
      }
    }
  }
};
